INSERT INTO predefined_responses (trigger_keywords, response_text, category, priority, is_production) VALUES 
(ARRAY['aransemen', 'buat', 'membuat', 'arrangement', 'create'], 'Saya dapat membantu Anda membuat aransemen! Arrangely memiliki editor chord & lirik yang intuitif dengan drag-and-drop untuk bagian lagu. Anda juga dapat menggunakan fitur AI untuk analisis lagu otomatis dari audio atau link YouTube. Apakah Anda ingin tahu lebih lanjut tentang fitur tertentu?', 'arrangements', 5, true),
(ARRAY['kolaborasi', 'tim', 'berbagi', 'collaboration', 'team'], 'Fitur kolaborasi Arrangely memungkinkan Anda mengundang anggota tim dengan akses berbasis peran. Anda dapat menambahkan komentar dan berkolaborasi secara real-time pada aransemen. Tim dapat bekerja bersama pada lagu yang sama dengan izin edit, view, atau admin. Butuh bantuan mengatur kolaborasi tim?', 'collaboration', 4, true),
(ARRAY['transpose', 'kunci', 'capo', 'key'], 'Fitur transpose instan memungkinkan Anda mengubah kunci lagu secara instan untuk vokalis yang berbeda. Anda akan mendapatkan rekomendasi capo dan saran chord yang disederhanakan. Cukup pilih kunci baru dan semua chord akan diperbarui otomatis!', 'transpose', 4, true),
(ARRAY['export', 'pdf', 'ekspor', 'unduh'], 'Anda dapat mengekspor aransemen ke PDF atau slide presentasi untuk tim Anda. Arrangely mendukung berbagai format ekspor termasuk sheet chord, setlist, dan slide untuk proyektor. Apakah Anda memerlukan bantuan dengan format ekspor tertentu?', 'export', 3, true),
(ARRAY['library', 'perpustakaan', 'menyimpan', 'save'], 'Perpustakaan komunitas Arrangely berisi ribuan aransemen profesional yang dibuat oleh musisi ahli. Dengan berlangganan, Anda dapat langsung menambahkan lagu apa pun ke perpustakaan pribadi Anda. Anda juga dapat mengatur lagu dalam folder dan menandai favorit.', 'library', 3, true);